  as = 'div',
  children,
  align = 'start',
  fullWidth,
  spacing = '4'
}) => {
  const className = classNames(styles.AlphaStack, fullWidth && styles.fullWidth, as === 'ul' && styles.listReset);
  const style = {
    '--pc-stack-align': align ? `${align}` : '',
    ...getResponsiveProps('stack', 'spacing', 'space', spacing)
  };
